update aset a.isflag=c.flag--select * from [dbo].[PMO_3410012_prd] ajoin (select b.assetnum,case when wocount=0 then 0	when wocount=1 then 1	when wocount>1 then  2 end FLAG from (select a.assetnum assetnum,count(b.wonum)wocountfrom [dbo].[PMO_3410012_prd] aleft join [dbo].Client_workorder bon a.assetnum=b.assetnum and a.siteid=b.siteid and b.DEV_WORKTYPE in ('PM','CM','RM')--where b.DEV_WORKTYPE in ('PM','CM','RM')group by a.assetnum)b)con a.assetnum=c.assetnum--insert into [dbo].[AX_Asset_Saving]--select * from [dbo].[AX_Asset_Saving_PRD]update AX_Asset_Savingset siteid='PRD'Select * from AX_Asset_Saving_PRDSelect * from AX_Asset_Saving where siteid='PRD'